class KKBox { 
double length ; 
double width ; 
double height ; 
double volume ; 
double surf ; 
KKBox ( double length , double width , double height ) { 
this . length = length ; 
this . width = width ; 
this . height = height ; 
} 
double vol ( double length , double width , double height ) { 
double volume = length * width * height ; 
return volume ; 
} 
double volume ( ) { 
return volume ; 
} 
double sur ( double length , double width , double height ) { 
double surf = 2 * length * width + width * height + height * length ; 
return surf ; 
} 
double surfaceArea ( ) { 
return surf ; 
} 
