System . out . print ( "請輸入收信人地址:" ) ; 
receiverAddr = sc . next ( ) ; 
} 
void display ( ) { 
System . out . print ( senderAddr + "," + sender ) ; 
System . out . print ( receiverAddr + "," + receiver + " " + title ) ; 
} 
}

