}

public class Envelop { 
String senderAddr ; 
String receiverAddr ; 
Letter letter ; 
Envelop ( String senderAddr , String receiverAddr , Letter letter ) { 
this . senderAddr = senderAddr ; 
this . receiverAddr = receiverAddr ; 
this . letter = letter ; 
} 
void display ( ) { 
System . out . println ( "From: " + senderAddr + "," + letter . getSender ( ) ) ; 
System . out . println ( "To: :" + receiverAddr + "," + letter . getReceiver ( ) + letter . getTitle ( ) ) ; 
System . out . println ( "信件內容:" ) ; 
letter . display ( ) ; 
} 
}

abstract public class Letter { 
String sender ; 
String receiver ; 
String title ; 
void display ( ) { 
} 
String getSender ( ) { 
return sender ; 
} 
String getReceiver ( ) { 
return receiver ; 
} 
String getTitle ( ) { 
return title ; 
} 
}

