abstract public class Evaluation { 
protected String id ; 
protected String type ; 
protected String name ; 
protected double g ; 
protected double w ; 
public Evaluation ( String id , String name , double g , double w , String type ) { 
this . id = id ; 
this . name = name ; 
this . g = g ; 
this . w = w ; 
this . type = type ; 
} 
abstract public void A ( ) ; 
abstract public void B ( ) ; 
abstract public void Dis ( String a , String b ) ; 
public void display ( ) { 
System . out . println ( "種類 : " + type ) ; 
System . out . println ( "學生學號 : " + id ) ; 
System . out . println ( "學生姓名 : " + name ) ; 
System . out . println ( "成績 : " + g ) ; 
System . out . println ( "成績比重 : " + w ) ; 
} 
}

public class Exam extends Evaluation { 
public Exam ( String id , String name , double g , double w , String type ) { 
super ( id , name , g , w , type ) ; 
} 
public void A ( ) { 
System . out . println ( "請輸入考試日期" ) ; 
} 
public void B ( ) { 
System . out . println ( "請輸入考試範圍" ) ; 
} 
public void Dis ( String a , String b ) { 
System . out . println ( "考試日期 : " + a ) ; 
System . out . println ( "考試日期 : " + b ) ; 
} 
}

public class HomeWork extends Evaluation { 
public HomeWork ( String id , String name , double g , double w , String type ) { 
super ( id , name , g , w , type ) ; 
} 
public void A ( ) { 
System . out . println ( "請輸入截止日期" ) ; 
} 
public void B ( ) { 
System . out . println ( "請輸入作業描述" ) ; 
} 
public void Dis ( String a , String b ) { 
System . out . println ( "截止日期 : " + a ) ; 
System . out . println ( "作業描述 : " + b ) ; 
} 
}

public class Report extends Evaluation { 
public Report ( String id , String name , double g , double w , String type ) { 
super ( id , name , g , w , type ) ; 
} 
public void A ( ) { 
System . out . println ( "請輸入報告內容" ) ; 
} 
public void B ( ) { 
System . out . println ( "請輸入參考文獻" ) ; 
} 
public void Dis ( String a , String b ) { 
System . out . println ( "報告內容 : " + a ) ; 
System . out . println ( "參考文獻 : " + b ) ; 
} 
}

