import java . util . Scanner ; 
abstract public class Evaluation { 
protected String sNumber ; 
protected String sName ; 
protected double sGrade ; 
protected double sProportion ; 
Evaluation ( String sNumber , String sName , double sGrade , double sProportion ) { 
this . sNumber = sNumber ; 
this . sName = sName ; 
this . sGrade = sGrade ; 
this . sProportion = sProportion ; 
} 
void print ( ) { 
System . out . println ( "您輸入的是:" ) ; 
System . out . println ( "學生學號:" + sNumber ) ; 
System . out . println ( "學生姓名:" + sName ) ; 
System . out . println ( "成績:" + sGrade ) ; 
System . out . println ( "比重:" + sProportion ) ; 
} 
}

public class Exam extends Evaluation { 
protected String date ; 
protected String range ; 
public Exam ( String sNumber , String sName , double sGrade , double sProportion , String date , String range ) { 
super ( sNumber , sName , sGrade , sProportion ) ; 
this . date = date ; 
this . range = range ; 
} 
}

public class Homework extends Evaluation { 
protected String deadline ; 
protected String description ; 
public Homework ( String sNumber , String sName , double sGrade , double sProportion , String deadline , String description ) { 
super ( sNumber , sName , sGrade , sProportion ) ; 
this . deadline = deadline ; 
this . description = description ; 
} 
}

public class Report extends Evaluation { 
protected String content ; 
protected String references ; 
public Report ( String sNumber , String sName , double sGrade , double sProportion , String content , String references ) { 
super ( sNumber , sName , sGrade , sProportion ) ; 
this . content = content ; 
this . references = references ; 
} 
}

