abstract public class Evaluation { 
private static int count ; 
protected String pid , name , type ; 
protected Keeper keeper ; 
public Evaluation ( String p , String n , String t , Keeper k ) { 
pid = p ; 
name = n ; 
type = t ; 
count ++ ; 
keeper = k ; 
} 
public static int getcount ( ) { 
return count ; 
} 
}

public class Exam extends Evaluation { 
public Exam ( String p , String n , String t , Keeper k ) { 
super ( p , n , t , k ) ; 
} 
}

public class HomeWork extends Evaluation { 
public HomeWork ( String p , String n , String t , Keeper k ) { 
super ( p , n , t , k ) ; 
} 
}

public class Keeper { 
private String id , tel , name ; 
public Keeper ( String n , String i , String t ) { 
id = i ; 
tel = t ; 
name = n ; 
} 
public void display ( ) { 
System . out . println ( "飼主名稱 : " + name ) ; 
System . out . println ( "飼主身分證字號 : " + id ) ; 
System . out . println ( "飼主電話 : " + tel ) ; 
} 
}

public class Report extends Evaluation { 
public Report ( String p , String n , String t , Keeper k ) { 
super ( p , n , t , k ) ; 
} 
}

