@Override public int compareTo(Object other){
           Letter o = (Letter)other;
           int thisdate, otherdate;
           thisdate = DateToInt(this.getDate());
           otherdate = DateToInt(o.getDate());
           if(thisdate > otherdate)
                      return 1;
           else if(thisdate < otherdate)
                      return -1;
           else
                      return 0;
}
