public class ChineseLetter{
    String title,receiver,content,sender,date;
    ChineseLetter(String title, String receiver, String content, String sender, String date){
        this.title=title;
        this.receiver=receiver;
        this.content=content;
        this.sender=sender;
        this.date=date;
    }
    String getSender(){
        return sender;
    }
    String getReceiver(){
        return receiver;
    }
    String getDate(){
        return date;
    }
    void display(){
        System.out.println(receiver+" "+title+", 您好");
        System.out.println(content);
        System.out.println(sender+" 敬上");
        System.out.println(date);
    }
    public static void main(String[] args) {
        ChineseLetter lt = new ChineseLetter ("教授", "王", "很高興跟您連絡", "銘哥", "2019/01/05");
        lt.display(); 
    }
    /*物件資料: title (String)
    物件資料: receiver (String)
    物件資料: content (String)
    物件資料: sender (String)
    物件資料: date (String)
    物件動作: display() 顯示信件內容
    物件動作: String getSender() 取得sender
    物件動作: String getReceiver() 取得 receiver
    物件動作: String getDate() 取得 date
    建構子: ChineseLetter(String title, String receiver, String content, String sender, String date)*/
}
