import java.util.Scanner;
class PET {
    static int count;
    String pid;
    String name;
    String type;
	String keeperid;
	String tel;
	PET(String pid,String name, String type, String keeperid, String tel) {
		this.pid = pid;
		this.name = name;
		this.type=type;
		this.keeperid = keeperid;
		this.tel =tel;
		count++;
	}
     void speak(){
        String strData5;
       if(type.equals("狗"))
            System.out.println("你的寵物叫聲為:汪汪");
       else if(type.equals("貓"))
            System.out.println("你的寵物叫聲為:喵喵");    
    }
     void display(){
        System.out.println("你輸入的寵物種類為:" + type);
        System.out.println("你輸入的寵物編號為:" + pid);
        System.out.println("你輸入的寵物名稱為:" + name);
        System.out.println("你輸入的飼主身分證字號為:" + keeperid);
        System.out.println("你輸入的電話為:" + tel);
    }
    public static void main(String[] args) {
        String strData,strData1,strData2,strData3,strData4;
        int num;
        Scanner scanner = new Scanner(System.in);
        strData = scanner.nextLine();
        System.out.println("請輸入寵物種類：");    

        System.out.println("請輸入寵物編號：");
        strData1 = scanner.nextLine();            
        System.out.println("請輸入寵物名稱：");
        strData2 = scanner.nextLine();            
        System.out.println("請輸入飼主身分證字號："); 
        strData3 = scanner.nextLine();          
        System.out.println("請輸入飼主電話：");
        strData4 = scanner.nextLine();
		Pet pet = new Pet(strData,strData1,strData2,strData3,strData4);
		pet.speak(strData);
        pet.display();
    }
}
