import java . util . * ; 
public class EnglishLetter extends Letter { 
EnglishLetter ( String title , String receiver , String content , String sender , String date ) { 
super ( title , receiver , content , sender , date ) ; 
} 
public void display ( ) { 
System . out . println ( "Dear " + title + " " + receiver ) ; 
System . out . println ( content ) ; 
System . out . println ( sender ) ; 
System . out . println ( "Sincerely" ) ; 
System . out . println ( date ) ; 
} 
public static void main ( String [ ] args ) { 
EnglishLetter id = new EnglishLetter ( "Professor" , "Wang" , "I am glad to write to you!" , "J. J. Lin" , "2019/01/05" ) ; 
} 
}

public class Envelop { 
String senderAddr ; 
String receiverAddr ; 
Letter letter ; 
Envelop ( String senderAddr , String receiverAddr , Letter letter ) { 
this . senderAddr = senderAddr ; 
this . receiverAddr = receiverAddr ; 
this . letter = letter ; 
} 
void display ( ) { 
System . out . println ( "From:" + senderAddr + ",銘哥" ) ; 
System . out . println ( "To: " + receiverAddr + ",王教授" ) ; 
System . out . println ( "信息內容:" ) ; 
} 
String getSenderAddr ( ) { 
return senderAddr ; 
} 
String getReceiverAddr ( ) { 
return receiverAddr ; 
} 
Letter getLetter ( ) { 
return letter ; 
} 
public static void main ( String [ ] args ) { 
Letter it = new ChineseLetter ( "教授" , "王" , "很高興跟您連絡" , "銘哥" , "2019/01/05" ) ; 
Envelop env = new Envelop ( "Taipei" , "Kaohsiung" , it ) ; 
env . display ( ) ; 
it . display ( ) ; 
} 
}

import java . util . * ; 
public abstract class Letter { 
protected String title ; 
protected String receiver ; 
protected String content ; 
protected String sender ; 
protected String date ; 
protected String senderAddr ; 
protected String receiverAddr ; 
protected Letter letter ; 
public Letter ( String title , String receiver , String content , String sender , String date ) { 
this . title = title ; 
this . receiver = receiver ; 
this . content = content ; 
this . sender = sender ; 
this . date = date ; 
} 
public void display ( ) { 
} 
public String getSender ( ) { 
return sender ; 
} 
public String getReceiver ( ) { 
return receiver ; 
} 
public String getDate ( ) { 
return date ; 
} 
public static void main ( String [ ] args ) { 
Letter it = new ChineseLetter ( "教授" , "王" , "很高興跟您連絡" , "銘哥" , "2019/01/05" ) ; 
Letter id = new EnglishLetter ( "Professor" , "Wang" , "I am glad to write to you!" , "J. J. Lin" , "2019/01/05" ) ; 
it . display ( ) ; 
System . out . println ( "-------------------------------" ) ; 
id . display ( ) ; 
} 
}
