import java.util.Scanner;
class exam
{
    String s;
    exam()
    {
    }
    void getString(String a)
    {
    s=a;
    }
    int getlength()
    {
    int ans=0;
    ans=s.length();
    return ans;
    }


    static public void main(String[] args)
    {
        Scanner scanner = new Scanner(System.in);
        String a=scanner.nextLine();       
        exam s=new exam();
        s.getString(a);
        int ans=s.getlength();
        System.out.println("輸入的數字長度："+ans);
        char an[];
        an=new char[ans];
        int anw[];
        anw=new int[10];
        for(int i=0;i<10;i++)
        anw[i]=0;
        for(int i=0;i<ans;i++)
        {

            an[i]=a.charAt(i);

        }
        for(int i=0;i<ans;i++)
        {
            int e=(int)an[i]-48;
            anw[e]=anw[e]+1;




        }
        for(int i=0;i<10;i++)
        {
        System.out.println(i+":"+anw[i]);



        }
    }
}
