abstract public class Kindsoftest { 
protected String name , range ; 
protected int id , gread , date ; 
protected double gp ; 
public Kindsoftest ( String name , int id , int gread , int date , double gp , String range ) { 
this . id = id ; 
this . name = name ; 
this . gread = gread ; 
this . gp = gp ; 
this . date = date ; 
this . range = range ; 
} 
}

