public class ChineseLetter
{
     private String title;
     private String receiver;
     private String content;
     private String sender;
     private String date;
     ChineseLetter(String title, String receiver, String content, String sender, String date){
         this.title=title;
         this.receiver=receiver;
         this.content=content;
         this.sender=sender;
         this.date=date;
     }
     void display(){
         System.out.println(receiver+" "+title+", 您好\n"+content+"\n"+sender+" 敬上\n"+date);
     }
     String getSender(){
         return sender;
     }
     String getReceiver(){
         return receiver;
     }  
     String getDate(){
         return date;
     }
}

public class Test
{
    public static void main(String[] args){
            ChineseLetter le=new ChineseLetter("同學", "黃", "很高興跟您連絡", "彭", "2019/01/05");
            le.display();
        }
}
