public class Evaluation
{
    static Scanner keyboard = new Scanner(System.in);

    private static choosetype(){
        System.out.println("Choose evaluation:");
        System.out.println("1. Exam");
        System.out.println("2. HomeWork");
        System.out.println("3. Report");
        int c = 0;
      do {
          c = keyboard.nextInt();
        } while ((c>3) || (c<1));
      return mevaluation(c);
    }             

    private static Evaluation(int c){
        Evalution w=null;
        switch(c){
            case 1:
            w=new exam("Exam");
            break;
            case 2:
            w=new homework("Homework");
            break;
            case 3:
            w=new Report("Report");
            break;
        }
        return w;
    }

    String name;
    String number;
    double score;
    double dscore;//比重

    Evalution(String name,String number,double score,double dscore){
        this.name=name;
        this.number=number;
        this.score=score;
        this.dscore=dscore;
    }

    System.out.println("請輸入學生學號:")
}

public class Exam extends Evaluation
{

    public Exam(String Testday,String Testrange)
    {
        super(Testday,Testrange);
    }

}

public class HomeWork extends Evaluation
{

    public HomeWork(String Lastday,String HW)
    {
        super(Lastday,HW);
    }

}

public class Report extends Evaluation
{

    public Report(String Thing,String Source)
    {
        super(Thing,Source);
    }

}

