static void volume ( double x , double y , double z ) { 
double sum ; 
sum = x * y ; 
sum *= z ; 
System . out . println ( "體積:" , volume ) ; 
} 
static void surfaceArea ( double x , double y , double z ) { 
double num ; 
num = 2 * x * y + y * z + z * x ; 
System . out . println ( "表面積:" , surfaceArea ) ; 
} 
