abstract public class EvalutionE { 
protected String sid , name , date , range , deadline , discribe , content , literature , grade , proportion ; 
public EvalutionE ( String s , String n , String g , String p , String d , String r ) { 
sid = s ; 
name = n ; 
grade = g ; 
proportion = p ; 
date = d ; 
range = r ; 
} 
}

abstract public class EvalutionH { 
protected String sid , name , date , range , deadline , discribe , content , literature , grade , proportion ; 
public EvalutionH ( String s , String n , String g , String p , String dl , String dis ) { 
sid = s ; 
name = n ; 
grade = g ; 
proportion = p ; 
deadline = dl ; 
discribe = dis ; 
} 
}

abstract public class EvalutionR { 
protected String sid , name , date , range , deadline , discribe , content , literature , grade , proportion ; 
public EvalutionR ( String s , String n , String g , String p , String c , String l ) { 
sid = s ; 
name = n ; 
grade = g ; 
proportion = p ; 
content = c ; 
literature = l ; 
} 
}

public class Exam extends EvalutionE { 
public Exam ( String s , String n , String g , String p , String d , String r ) { 
super ( s , n , g , p , d , r ) ; 
} 
}

public class Homework extends EvalutionH { 
public Homework ( String s , String n , String g , String p , String dl , String dis ) { 
super ( s , n , g , p , dl , dis ) ; 
} 
}

public class Report extends EvalutionR { 
public Report ( String s , String n , String g , String p , String c , String l ) { 
super ( s , n , g , p , c , l ) ; 
} 
}

