public class Exam extends Score { 
public Exam ( int ggrade , double ppercentage , String ddate , String sscale ) { 
super ( ggrade , ppercentage , ddate , sscale ) ; 
kind = "考試" ; 
} 
}

public class Homework extends Score { 
public Homework ( int ggrade , double ppercentage , String ddate , String sscale ) { 
super ( ggrade , ppercentage , ddate , sscale ) ; 
kind = "作業" ; 
} 
}

public class Report extends Score { 
public Report ( int ggrade , double ppercentage , String ddate , String sscale ) { 
super ( ggrade , ppercentage , ddate , sscale ) ; 
kind = "心得報告" ; 
} 
}

abstract public class Score { 
protected int grade ; 
protected double percentage ; 
protected String date , scale , kind ; 
public Score ( ) { 
} 
public Score ( int ggrade , double ppercentage , String ddate , String sscale ) { 
grade = ggrade ; 
percentage = ppercentage ; 
date = ddate ; 
scale = sscale ; 
} 
public void print ( ) { 
System . out . println ( "種類: " + kind ) ; 
System . out . println ( "成績：" + grade ) ; 
System . out . println ( "成績比重：" + percentage ) ; 
System . out . println ( "考試日期: " + date ) ; 
System . out . println ( "考試範圍: " + scale ) ; 
} 
}

public class Student { 
protected String Num , Name ; 
public Student ( String nnum , String nname ) { 
Num = nnum ; 
Name = nname ; 
} 
public void print ( ) { 
System . out . println ( "學生學號：" + Num ) ; 
System . out . println ( "學生姓名: " + Name ) ; 
} 
}

